import React                from 'react';
import PropTypes            from 'prop-types';
import AnimatedBackground   from '../AnimatedBackground';

export default class LinesPlaceholder extends React.Component {
  static propTypes = {
    rows: PropTypes.number,
    lineHeight: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    lineSpacing: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
  }

  render() {
    return (
      <div>
        {[...Array(this.props.rows || 8)].map((v,i) => {
          return (
            <div key={i} style={{ marginBottom: `${this.props.lineSpacing || 16}px` }}>
              <AnimatedBackground height={30} />
            </div>
          )
        })}
      </div>
    );
  }
}
